class Matrix{constructor(t,s){this.rows=t,this.cols=s,this.data=[];for(let t=0;t<this.rows;t++){this.data[t]=[];for(let s=0;s<this.cols;s++)this.data[t][s]=0}}static fromArray(t){if(t instanceof Array==0)return;let s=new Matrix(t.length,1);for(let o=0;o<t.length;o++)s.data[o][0]=t[o];return s}toArray(){let t=[];for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)t.push(this.data[s][o]);return t}print(){console.table(this.data)}randomize(t,s){null!=s&&""!=s||(s=10),null!=t&&""!=t||(t=0);for(let o=0;o<this.rows;o++)for(let r=0;r<this.cols;r++)this.data[o][r]=Math.floor(Math.random()*(s-t))+t}floatRandomize(t,s){null!=s&&""!=s||(s=1),null!=t&&""!=t||(t=-1);for(let o=0;o<this.rows;o++)for(let r=0;r<this.cols;r++)this.data[o][r]=Math.random()*(s-t)+t}transpose(){let t=new Matrix(this.cols,this.rows);for(let s=0;s<t.rows;s++)for(let o=0;o<t.cols;o++)t.data[s][o]=this.data[o][s];this.data=t.data}static transpose(t){if(t instanceof Matrix==0)return void console.error("The multiply function must contain two Matrix !");let s=new Matrix(t.cols,t.rows);for(let o=0;o<s.rows;o++)for(let r=0;r<s.cols;r++)s.data[o][r]=t.data[r][o];return s}map(t){for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++){const r=this.data[s][o];this.data[s][o]=t(r)}}static map(t,s){let o=new Matrix(t.rows,t.cols);for(let r=0;r<o.rows;r++)for(let a=0;a<o.cols;a++){const l=t.data[r][a];o.data[r][a]=s(l)}return o}add(t){if(t instanceof Matrix)for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)this.data[s][o]+=t.data[s][o];else for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)this.data[s][o]+=t}static sub(t,s){let o=new Matrix(t.rows,t.cols);for(let r=0;r<o.rows;r++)for(let a=0;a<o.cols;a++)o.data[r][a]=t.data[r][a]-s.data[r][a];return o}dot(t){if(t instanceof Matrix)for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)this.data[s][o]*=t.data[s][o];else for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)this.data[s][o]*=t}static dot(t,s){if(t instanceof Matrix!=0&&s instanceof Matrix!=0){if(t.cols==s.rows){var o=new Matrix(t.rows,s.cols);for(let r=0;r<o.rows;r++)for(let a=0;a<o.cols;a++){let l=0;for(let o=0;o<t.cols;o++)l+=t.data[r][o]*s.data[o][a];o.data[r][a]=l}return o}console.error("The first matrix cols must match the second matrix rows !")}else console.error("The multiply function must contain two Matrix !")}copy(){let t=new Matrix(this.rows,this.cols);for(let s=0;s<this.rows;s++)for(let o=0;o<this.cols;o++)t.data[s][o]=this.data[s][o];return t}}
